let value: string | number;
value = "hello";
console.log(value);
value = 42;
console.log(value);

What will be the output of the following TypeScript code?
interface User {
  name: string;
  age: number;
}
let user: User = { name: "Alice", age: 25 };
console.log(user);

What will be the output of the following TypeScript code?
interface Shape {
  color: string;
}
interface Square extends Shape {
  sideLength: number;
}
let square: Square = { color: "blue", sideLength: 10 };
console.log(square);
